def welcome
  puts "Hello fellas! Bienvenue dans mon app"
end

def play
  welcome
  question_1
end

def intermezo
  puts ""
end

def press_enter
    print "Tape la touche entrée pour voir la réponse "
    input = gets.chomp
    return input
end

def question_1
  intermezo
  puts "Combien y a-t-il de journalistes dans ce array ?"
  input = press_enter
  if input == ""
    puts journalists.count
    question_2
  else
    question_1
  end
end

def question_2
  intermezo
  puts "Combien d'handle contiennent un numéro ?"
  input = press_enter
  if input == ""
  puts journalists.select {|element| element.match(/\d/)}.count
    question_3
  else
    question_2
  end
end

def question_3
  intermezo
  puts "Combien d'handle contiennent les 4 lettres du prénom 'Aude' à la suite (sans prendre en compte les majuscules) ?"
  input = press_enter
  if input == ""
  puts journalists.select {|element| element.downcase.include? "aude" }.count
    question_4
  else
    question_3
  end
end

def question_4
  intermezo
  puts "Combien commencent par une majuscule (première lettre juste après le @) ?"
  input = press_enter
  if input == ""
  puts journalists.select {|element| element.match(/@[A-Z]/) }.count
    question_5
  else
    question_4
  end
end

def question_5
  intermezo
  puts "Combien contiennent au moins une majuscule ?"
  input = press_enter
  if input == ""
  puts journalists.select {|element| element.match(/[A-Z]/) }.count
    question_6
  else
    question_5
  end
end

def question_6
  intermezo
  puts "Combien y a-t-il de underscore _ dans tous les pseudos confondus ?"
  input = press_enter
  if input == ""
  puts journalists.select {|element| element.match(/_/) }.count
    question_7
  else
    question_6
  end
end

def question_7
  intermezo
  puts "Trie la liste de handle par ordre alphabétique."
  input = press_enter
  if input == ""
  p journalists.sort_by { |element| element.downcase }
    question_8
  else
    question_7
  end
end

def question_8
  intermezo
  puts "Quels sont les 50 handles les plus courts de ce array ?"
  input = press_enter
  if input == ""
  p journalists.sort_by { |element| element.length}.take(50)
    question_9
  else
    question_8
  end
end

def question_9
  intermezo
  puts "Quelle est la position dans l'array de la personne @epenser ?"
  input = press_enter
  if input == ""
  puts "@epenser est en #{journalists.index("@epenser")+1}ème position."
    ending
  else
    question_9
  end
end

def ending
  intermezo
  puts "YOU ROCK!!"
end

play







